mapper.select:
SELECT *
FROM PAY_TRADE

    BP_ORDER_ID =#{bpOrderId} AND BP_ID = #{bpId}

SELECT *
FROM PAY_TRADE

    ORDER_TRADE_NO =#{orderTradeNo} AND BP_ID = #{bpId}

mapper.update:
UPDATE PAY_TRADE
STATUS =#{status}, PAY_DATE=#{payDate,jdbcType=BIGINT}, PAY_DATE_FORMAT=#{payDateFormat, jdbcType=DATE}, PAY_SERIAL_NUMBER= #{paySerialNumber, jdbcType=VARCHAR}, PAY_DATE_DAY=#{payDateDay}, PAY_DATE_MONTH=#{payDateMonth}, PAY_DATE_QUARTER=#{payDateQuarter,jdbcType=BIGINT}, COUPON_FEE=#{couponFee,jdbcType=DOUBLE}
, PLATFORM_PAY_DATE=#{platformPayDate}
,PLATFORM_PAY_FORMAT=#{platformPayFormat}
ORDER_TRADE_NO = #{orderTradeNo, jdbcType=VARCHAR}

UPDATE
PAY_TRADE

    STATUS =#{status}


    ORDER_TRADE_NO =#{orderTradeNo, jdbcType=VARCHAR}
    AND STATUS = 1
